class MageWorx_Adminhtml_Block_Multifees_Adminhtml_Sales_Order_Fee extends Mage_Adminhtml_Block_Sales_Items_Abstract
{
	public function __construct()
    {
        parent::__construct();
        $this->setTemplate('multifees/details-fee.phtml');
    }

	public function getOrder()
    {
    	if ($this->hasOrder()) {
            return $this->getData('order');
        }
        if (Mage::registry('current_order')) {
            return Mage::registry('current_order');
        }
        if (Mage::registry('order')) {
            return Mage::registry('order');
        }
    	if (Mage::registry('current_invoice')) {
    		return Mage::registry('current_invoice')->getOrder();
    	}
    	if (Mage::registry('current_creditmemo')) {
    		return Mage::registry('current_creditmemo')->getOrder();
    	}
    }
}